     public JSONObject convertExcelToJSON(String excelFilePath) throws Exception 
     {   
        FileInputStream fileInputStream = new FileInputStream(new File(excelFilePath));
        DataFormatter dataFormatter = new DataFormatter();
        Workbook workbook = new XSSFWorkbook(fileInputStream);
        Sheet sheet = workbook.getSheetAt(0);
        List<String> headers = new ArrayList<>();
        Row headerRow = sheet.getRow(0);

        for (Cell cell : headerRow) {
            headers.add(cell.getStringCellValue());
        }
       System.out.println("Row count "+ sheet.getLastRowNum());
       int records=0;
        JSONArray jsonArray = new JSONArray();
        for (int i = 1; i <= sheet.getLastRowNum(); i++) 
        {
            Row row = sheet.getRow(i);
            JSONObject jsonObject = new JSONObject();
            Cell cellcheck = row.getCell(0);
            if (dataFormatter.formatCellValue(cellcheck)!="" )

            {
                records++;
            for (int j = 0; j < headers.size(); j++) 
            {
                Cell cell = row.getCell(j);
                if (cell != null) 
                {

                  jsonObject.put(headers.get(j), dataFormatter.formatCellValue(cell));

                }
            }
            jsonArray.put(jsonObject);
        }

        }


        JSONObject finalJson = new JSONObject();

        finalJson.put(sheet.getSheetName(), jsonArray);

        workbook.close();
        fileInputStream.close();
        System.out.println("Total Records "+ records);
         return finalJson;


    }

