public class MCCObjParser 
{

    public List<MCCTargetObjStructure> ConvertSourceObjectToTargetObject(List<MCCSourceObjStructure> sourceObj)
    {

        List<MCCTargetObjStructure> mccTargetObjectStructures = new ArrayList<>();
        int targetObjCounter=0;
        for(int i=0; i < sourceObj.size(); i++)
        {
           if(sourceObj.get(i).getLastAsset() == null)
           {
            sourceObj.get(i).setLastAsset("N");
           }

           if((i==0) || (sourceObj.get(i-1).getLastAsset().equalsIgnoreCase("Y") && sourceObj.get(i).getLastAsset().equalsIgnoreCase("Y")) || (sourceObj.get(i-1).getLastAsset().equalsIgnoreCase("Y") && !sourceObj.get(i).getLastAsset().equalsIgnoreCase("Y"))  )
           {
           MCCTargetObjStructure targetObj = new MCCTargetObjStructure();
           targetObj.deviceModulePlanDetails = new ArrayList<>();
           targetObj.setCycleId(sourceObj.get(i).getCycleId());
           targetObj.setCycleName(sourceObj.get(i).getCycleName());
           targetObj.setCycleName(sourceObj.get(i).getCycleName());
           targetObj.setCycleDetails(sourceObj.get(i).getCycleDetails());
           targetObj.setProjectShortName(sourceObj.get(i).getShortName());
           targetObj.setAccountName(sourceObj.get(i).getShortName());
           targetObj.setScheduledStartDate(sourceObj.get(i).getStartDate());
           targetObj.setScheduledEndDate(sourceObj.get(i).getEndDate());
           targetObj.setAssignedToEmail(sourceObj.get(i).getAssignedToEmail());
           targetObj.setApproverEmail(sourceObj.get(i).getApproverEmail());
           targetObj.setCrewName(sourceObj.get(i).getCrewName());
           targetObj.setAccountName(sourceObj.get(i).getAccountName());
           MCCDeviceModulePlanDetails deviceModulePlanDetail = new MCCDeviceModulePlanDetails();
           deviceModulePlanDetail.setAssetName(sourceObj.get(i).getAssetName());
           deviceModulePlanDetail.setAssetCode(sourceObj.get(i).getAssetName());
           deviceModulePlanDetail.setPlannedNoOfModules(sourceObj.get(i).getPlannedNumberofModules());
           deviceModulePlanDetail.setPlannedDate(sourceObj.get(i).getPlannedDate());
           deviceModulePlanDetail.setRemarks(sourceObj.get(i).getRemarks());
           targetObj.deviceModulePlanDetails.add(deviceModulePlanDetail);
           mccTargetObjectStructures.add(targetObj);
           targetObjCounter=targetObjCounter+1;
           }

          else if ((!sourceObj.get(i-1).getLastAsset().equalsIgnoreCase("Y") && !sourceObj.get(i).getLastAsset().equalsIgnoreCase("Y")) || (!sourceObj.get(i-1).getLastAsset().equalsIgnoreCase("Y") && sourceObj.get(i).getLastAsset().equalsIgnoreCase("Y")))
           {

           MCCDeviceModulePlanDetails deviceModulePlanDetail = new MCCDeviceModulePlanDetails();
           deviceModulePlanDetail.setAssetName(sourceObj.get(i).getAssetName());
           deviceModulePlanDetail.setAssetCode(sourceObj.get(i).getAssetName());
           deviceModulePlanDetail.setPlannedNoOfModules(sourceObj.get(i).getPlannedNumberofModules());
           deviceModulePlanDetail.setPlannedDate(sourceObj.get(i).getPlannedDate());
           deviceModulePlanDetail.setRemarks(sourceObj.get(i).getRemarks());
           mccTargetObjectStructures.get(targetObjCounter-1).deviceModulePlanDetails.add(deviceModulePlanDetail);
           }
    }
        return mccTargetObjectStructures;


    }

}

